INSERT INTO `page` (`id`, `menu_id`, `page_name`, `page_slug`, `page_content`, `page_position`) VALUES
(1, 1, 'Khoa CNTT', 'khoa-cntt', '<p style=\\"text-align: center;\\"><strong>GIỚI THIỆU KHOA C&Ocirc;NG NGHỆ TH&Ocirc;NG TIN - ĐẠI HỌC TH&Agrave;NH Đ&Ocirc;</strong></p>\r\n<p style=\\"text-align: center;\\"><strong><img src=\\"/source/users/1/images/khoa-cntt/gioi-thieu-chung/khoa_cntt_thanh_do.jpg?1461336288400\\" alt=\\"khoa_cntt_thanh_do\\" /></strong></p>\r\n<p><strong> T&ecirc;n Khoa: </strong>Khoa C&ocirc;ng nghệ th&ocirc;ng tin (CNTT)</p>\r\n<p><strong> Địa chỉ v&agrave; điện thoại li&ecirc;n hệ:</strong></p>\r\n<p>- Địa chỉ: Tầng 4, Nh&agrave; B, Trường Đại học Th&agrave;nh Đ&ocirc;, Kim Chung, Ho&agrave;i Đức, H&agrave; Nội</p>\r\n<p>- Điện thoại: 0433. 861.763 &ndash; M&aacute;y lẻ: 305</p>\r\n<p>- Email: Khoa.<a href=\\"mailto:CNTT@thanhdo.edu.vn\\">CNTT@thanhdo.edu.vn</a>&nbsp; - Website: <a href=\\"http://www.thanhdo.edu.vn/\\">http://www.thanhdo.edu.vn</a></p>\r\n<p><strong>Qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh ph&aacute;t triển</strong></p>\r\n<p>Khoa C&ocirc;ng nghệ Th&ocirc;ng tin của Trường Đại học Th&agrave;nh Đ&ocirc; được ch&iacute;nh thức mang t&ecirc;n từ th&aacute;ng 6/2009 v&agrave; l&agrave; sự ph&aacute;t triển tiếp nối của Khoa C&ocirc;ng nghệ Th&ocirc;ng tin Trường Cao đẳng Tư thục C&ocirc;ng nghệ Th&agrave;nh Đ&ocirc; th&agrave;nh lập ng&agrave;y 30/11/2004. Sự ph&aacute;t triển của Khoa gắn liền với sự ph&aacute;t triển của Trường trong suốt nhưng năm qua v&agrave; đang nỗ lực vươn l&ecirc;n kh&ocirc;ng ngừng.</p>\r\n<p style=\\"text-align: center;\\"><img src=\\"/source/users/1/images/khoa-cntt/gioi-thieu-chung/giang_vien_khoa_cntt_thanh_do.jpg?1461336465311\\" alt=\\"giang_vien_khoa_cntt_thanh_do\\" width=\\"671\\" height=\\"345\\" /></p>\r\n<p style=\\"text-align: center;\\"><em>NGND. Ng&ocirc; Xu&acirc;n Độ, TSKH. Phạm Trần Nhu c&ugrave;ng c&aacute;n bộ giảng vi&ecirc;n trẻ của Khoa</em></p>\r\n<p><strong>Mục ti&ecirc;u v&agrave; chương tr&igrave;nh đ&agrave;o tạo:</strong></p>\r\n<p>Khoa CNTT hướng sinh vi&ecirc;n đến mục ti&ecirc;u <strong><em>&ldquo;Học để l&agrave;m được việc&rdquo;</em></strong>. Chương tr&igrave;nh đ&agrave;o tạo kh&ocirc;ng ngừng đổi mới với mục ti&ecirc;u mỗi sinh vi&ecirc;n Đại học Th&agrave;nh Đ&ocirc; khi ra trường đều tự tin với kỹ năng tin học của m&igrave;nh. Mỗi sinh vi&ecirc;n chuy&ecirc;n ng&agrave;nh c&ocirc;ng nghệ th&ocirc;ng tin của Nh&agrave; trường đều c&oacute; thể đ&aacute;p ứng c&aacute;c c&ocirc;ng việc li&ecirc;n quan đến ng&agrave;nh học. C&aacute;c học phần li&ecirc;n tục được cập nhật v&agrave; bổ sung nhằm đ&aacute;p ứng tốt nhất nhu cầu của thị trường lao động đ&ograve;i hỏi ng&agrave;y c&agrave;ng cao trước &aacute;p lực cạnh tranh to&agrave;n cầu.</p>\r\n<p><strong><em>C&aacute;c hệ đ&agrave;o tạo v&agrave; chuy&ecirc;n ng&agrave;nh:&nbsp;</em></strong></p>\r\n<table width=\\"100%\\">\r\n<thead>\r\n<tr>\r\n<td width=\\"36\\">\r\n<p><strong>Stt</strong></p>\r\n</td>\r\n<td width=\\"239\\">\r\n<p><strong>Hệ đ&agrave;o tạo</strong></p>\r\n</td>\r\n<td width=\\"159\\">\r\n<p><strong>Chuy&ecirc;n ng&agrave;nh</strong></p>\r\n</td>\r\n<td width=\\"226\\">\r\n<p><strong>Cấp bằng</strong></p>\r\n</td>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td width=\\"36\\">\r\n<p>1</p>\r\n</td>\r\n<td width=\\"239\\">\r\n<p>Đại học Ch&iacute;nh quy</p>\r\n</td>\r\n<td width=\\"159\\">\r\n<p>C&ocirc;ng nghệ th&ocirc;ng tin</p>\r\n</td>\r\n<td width=\\"226\\">\r\n<p>Kỹ sư C&ocirc;ng nghệ th&ocirc;ng tin</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\\"36\\">\r\n<p>2</p>\r\n</td>\r\n<td width=\\"239\\">\r\n<p>Đại học Ch&iacute;nh quy li&ecirc;n th&ocirc;ng từ Cao đẳng ch&iacute;nh quy</p>\r\n</td>\r\n<td width=\\"159\\">\r\n<p>C&ocirc;ng nghệ th&ocirc;ng tin</p>\r\n</td>\r\n<td width=\\"226\\">\r\n<p>Kỹ sư C&ocirc;ng nghệ th&ocirc;ng tin</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\\"36\\">\r\n<p>3</p>\r\n</td>\r\n<td width=\\"239\\">\r\n<p>Đại học Ch&iacute;nh quy li&ecirc;n th&ocirc;ng từ Cao đẳng nghề</p>\r\n</td>\r\n<td width=\\"159\\">\r\n<p>C&ocirc;ng nghệ th&ocirc;ng tin</p>\r\n</td>\r\n<td width=\\"226\\">\r\n<p>Kỹ sư C&ocirc;ng nghệ th&ocirc;ng tin</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\\"36\\">\r\n<p>4</p>\r\n</td>\r\n<td width=\\"239\\">\r\n<p>Cao đẳng Ch&iacute;nh quy</p>\r\n</td>\r\n<td width=\\"159\\">\r\n<p>Tin học ứng dụng</p>\r\n</td>\r\n<td width=\\"226\\">\r\n<p>Cử nh&acirc;n Tin học ứng dụng</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\\"36\\">\r\n<p>5</p>\r\n</td>\r\n<td width=\\"239\\">\r\n<p>Cao đẳng Nghề</p>\r\n</td>\r\n<td width=\\"159\\">\r\n<p>Lập tr&igrave;nh m&aacute;y t&iacute;nh</p>\r\n</td>\r\n<td width=\\"226\\">\r\n<p>Cử nh&acirc;n Lập tr&igrave;nh m&aacute;y t&iacute;nh</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong>Chức năng, nhiệm vụ</strong>&nbsp;&nbsp;</p>\r\n<p>Khoa CNTT Trường Đại học Th&agrave;nh Đ&ocirc; c&oacute; chức năng đ&agrave;o tạo Kỹ sư c&aacute;c hệ Đại học ch&iacute;nh quy, Đại học li&ecirc;n th&ocirc;ng ch&iacute;nh quy, Cử nh&acirc;n Cao đẳng ch&iacute;nh quy v&agrave; Cao đẳng nghề với c&aacute;c chuy&ecirc;n ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin, Tin học ứng dụng v&agrave; Lập tr&igrave;nh m&aacute;y t&iacute;nh.</p>\r\n<p>Khoa thực hiện nhiệm vụ x&acirc;y dựng chương tr&igrave;nh đ&agrave;o tạo, x&acirc;y dựng đội ngũ c&aacute;n bộ giảng vi&ecirc;n c&oacute; năng lực v&agrave; tr&igrave;nh độ chuy&ecirc;n m&ocirc;n cao, tổ chức triển khai đ&agrave;o tạo theo kế hoạch v&agrave; l&agrave;m tốt c&ocirc;ng t&aacute;c nghi&ecirc;n cứu khoa học trong lĩnh vực CNTT. Đảm bảo khi tốt nghiệp sinh vi&ecirc;n của Khoa được trang bị c&aacute;c kiến thức cơ bản v&agrave; chuy&ecirc;n ng&agrave;nh vững v&agrave;ng, c&oacute; khả năng l&agrave;m việc thực tiễn, tiếp cận v&agrave; l&agrave;m chủ c&aacute;c c&ocirc;ng nghệ ti&ecirc;n tiến.</p>\r\n<p><strong>Đội ngũ giảng vi&ecirc;n:</strong></p>\r\n<p>Với đội ngũ trẻ, năng động, đầy nhiệt huyết, giảng vi&ecirc;n Khoa CNTT đang từng bước khẳng định thương hiệu của m&igrave;nh qua chất lượng đ&agrave;o tạo. Tập thể giảng vi&ecirc;n kh&ocirc;ng ngừng học tập n&acirc;ng cao tr&igrave;nh độ, cập nhật c&ocirc;ng nghệ để theo kịp sự thay đổi như vũ b&atilde;o của CNTT.</p>\r\n<p>100% giảng vi&ecirc;n của Khoa c&oacute; tr&igrave;nh độ sau đại học, trong đ&oacute; số giảng vi&ecirc;n c&oacute; tr&igrave;nh độ Tiến sĩ chiếm tr&ecirc;n 20%.</p>\r\n<p>Khoa thường xuy&ecirc;n hợp t&aacute;c với đội ngũ giảng vi&ecirc;n đến từ c&aacute;c Viện nghi&ecirc;n cứu, c&aacute;c trường Đại học v&agrave; c&aacute;c doanh nghiệp CNTT để giảng dạy, trao đổi kinh nghiệm cho tập thể sư phạm của khoa v&agrave; gi&uacute;p sinh vi&ecirc;n chuy&ecirc;n ng&agrave;nh CNTT tiếp cận c&ocirc;ng nghệ mới cũng như cập nhật c&aacute;c phần mềm mới.</p>\r\n<p><strong> Cơ sở vật chất:</strong></p>\r\n<p>Khoa CNTT được trang bị hơn 300 m&aacute;y t&iacute;nh cấu h&igrave;nh cao với 06 ph&ograve;ng thực h&agrave;nh c&oacute; đầy đủ c&aacute;c thiết bị hiện đại đ&aacute;p ứng đầy đủ nhu cầu học tập v&agrave; nghi&ecirc;n cứu của sinh vi&ecirc;n, giảng vi&ecirc;n trong Khoa. Hệ thống mạng Wifi phủ k&iacute;n to&agrave;n bộ khu vực học tập của sinh vi&ecirc;n. Phần mềm phục vụ giảng dạy v&agrave; học tập li&ecirc;n tục cập nhật phi&ecirc;n bản mới.</p>\r\n<p style=\\"text-align: center;\\"><img src=\\"/source/users/1/images/khoa-cntt/gioi-thieu-chung/csvc_khoa_cntt_thanh_do.jpg?1461336668596\\" alt=\\"csvc_khoa_cntt_thanh_do\\" width=\\"670\\" height=\\"341\\" /></p>\r\n<p><strong> Th&agrave;nh t&iacute;ch nổi bật:</strong></p>\r\n<p>Ba năm liền (2012, 2013, 2014) tập thể Khoa được tăng danh hiệu Tập thể lao động ti&ecirc;n tiến. Tr&ecirc;n 90% c&aacute;n bộ giảng vi&ecirc;n trong khoa đạt danh hiệu Lao động ti&ecirc;n tiến.</p>\r\n<p>Hai giảng vi&ecirc;n trong khoa đ&atilde; đạt giải cao trong c&aacute;c cuộc thi tay nghề do Tổng cục dạy nghề tổ chức.</p>\r\n<p>Đội lập tr&igrave;nh Android lần đầu ti&ecirc;n tham gia đ&atilde; lọt v&agrave;o top 5 đội mạnh trong cuộc thi x&acirc;y dựng ứng dụng điểu khiển robot (S.M.A.C Changlege) do Tập đo&agrave;n FPT tổ chức năm 2014.</p>\r\n<p>C&aacute;c sinh vi&ecirc;n trong Khoa đ&atilde; t&iacute;ch cực tham gia c&aacute;c phong tr&agrave;o do Đo&agrave;n, Hội tổ chức v&agrave; đạt nhiều giải cao.</p>\r\n<p><strong>Cơ hội việc l&agrave;m:</strong></p>\r\n<p>Đảng v&agrave; Nh&agrave; nước đ&atilde; x&aacute;c định CNTT đ&oacute;ng vai tr&ograve; đặc biệt cho sự ph&aacute;t triển kinh tế, x&atilde; hội v&agrave; đảm bảo an ninh quốc ph&ograve;ng, l&agrave; <em>&ldquo;hạ tầng của hạ tầng&rdquo;</em>. CNTT l&agrave; ng&agrave;nh kinh tế mũi nhọn mang t&iacute;nh đột ph&aacute;. Theo dự b&aacute;o v&agrave; nhu cầu thực tế của nền kinh tế Việt Nam, h&agrave;ng năm nước ta cần nguồn nh&acirc;n lực CNTT (năm 2015 cần hơn 400.000 kỹ sư CNTT, đến năm 2020 cần đến hơn 1 triệu kỹ sư CNTT). Ng&agrave;nh CNTT được đ&aacute;nh gi&aacute; l&agrave; ng&agrave;nh c&oacute; mức thu nhập b&igrave;nh qu&acirc;n cao nhất trong c&aacute;c ng&agrave;nh nghề hiện nay.</p>\r\n<p>Mỗi năm Khoa CNTT- Đại học Th&agrave;nh Đ&ocirc; cung cấp cho thị trường lao động hơn một trăm kỹ sư CNTT, cử nh&acirc;n Tin học ứng dụng v&agrave; cử nh&acirc;n Nghề lập tr&igrave;nh m&aacute;y t&iacute;nh. Đa số sinh vi&ecirc;n ra trường tiếp tục theo đuổi đam m&ecirc; c&ocirc;ng nghệ trong nhiều lĩnh vực kh&aacute;c nhau như c&ocirc;ng nghệ phần mềm, ph&acirc;n t&iacute;ch thiết kế hệ thống, ph&aacute;t triển web, thiết kế web, ph&aacute;t triển ứng dụng di động,&hellip; nhiều sinh vi&ecirc;n đ&atilde; đảm nhiệm c&aacute;c vị tr&iacute; quan trọng tại nơi l&agrave;m việc.</p>\r\n<p>Đặc biệt, sinh vi&ecirc;n tốt nghiệp khoa CNTT- ĐH Th&agrave;nh Đ&ocirc; c&oacute; đủ khả năng v&agrave; tự tin để x&acirc;y dựng c&aacute;c ứng dụng tr&ecirc;n điện thoại với c&aacute;c nền tảng Android, Windows Phone v&agrave; iOS.</p>\r\n<p>Khoa CNTT phấn đấu c&ugrave;ng Trường Đại học Th&agrave;nh Đ&ocirc; thực hiện th&agrave;nh c&ocirc;ng phương ch&acirc;m <strong><em>&ldquo;Uy t&iacute;n &ndash; Chất lương &ndash; Hội nhập &ndash; Ph&aacute;t triển&rdquo;.</em></strong></p>', 10),
(4, 0, 'Sinh viên', 'sinh-vien', '<p>Nội dung trang sinh vi&ecirc;n</p>', 50),
(5, 0, 'Giảng viên', 'giang-vien', '<p style=\\"text-align: center;\\"><strong>ĐỘI NGŨ GIẢNG VI&Ecirc;N</strong></p>\r\n<p style=\\"text-align: center;\\"><strong><img src=\\"/source/users/1/images/khoa-cntt/gioi-thieu-chung/giang_vien_khoa_cntt_thanh_do.jpg?1461337391251\\" alt=\\"giang_vien_khoa_cntt_thanh_do\\" width=\\"671\\" height=\\"345\\" /></strong></p>\r\n<p>Với đội ngũ trẻ, năng động, đầy nhiệt huyết, giảng vi&ecirc;n Khoa CNTT đang từng bước khẳng định thương hiệu của m&igrave;nh qua chất lượng đ&agrave;o tạo. Tập thể giảng vi&ecirc;n kh&ocirc;ng ngừng học tập n&acirc;ng cao tr&igrave;nh độ, cập nhật c&ocirc;ng nghệ để theo kịp sự thay đổi như vũ b&atilde;o của CNTT.</p>\r\n<p>100% giảng vi&ecirc;n của Khoa c&oacute; tr&igrave;nh độ sau đại học, trong đ&oacute; số giảng vi&ecirc;n c&oacute; tr&igrave;nh độ Tiến sĩ chiếm tr&ecirc;n 20%.</p>\r\n<p>Khoa thường xuy&ecirc;n hợp t&aacute;c với đội ngũ giảng vi&ecirc;n đến từ c&aacute;c Viện nghi&ecirc;n cứu, c&aacute;c trường Đại học v&agrave; c&aacute;c doanh nghiệp CNTT để giảng dạy, trao đổi kinh nghiệm cho tập thể sư phạm của khoa v&agrave; gi&uacute;p sinh vi&ecirc;n chuy&ecirc;n ng&agrave;nh CNTT tiếp cận c&ocirc;ng nghệ mới cũng như cập nhật c&aacute;c phần mềm mới.</p>\r\n<table style=\\"height: 15px; margin-left: auto; margin-right: auto;\\" width=\\"541\\">\r\n<tbody>\r\n<tr>\r\n<td style=\\"width: 262px;\\"><img src=\\"/source/users/1/images/khoa-cntt/giang-vien/txthanh.jpg?1461337511404\\" alt=\\"txthanh\\" width=\\"155\\" height=\\"218\\" /></td>\r\n<td style=\\"width: 263px;\\"><strong>ThS. Trần Xu&acirc;n Thanh, Trưởng Khoa</strong>&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td style=\\"width: 262px;\\"><img src=\\"/source/users/1/images/khoa-cntt/giang-vien/nvdien.jpg?1461337541215\\" alt=\\"nvdien\\" width=\\"141\\" height=\\"188\\" /></td>\r\n<td style=\\"width: 263px;\\"><strong>KS (Cao học). Nguyễn Văn Diễn, Gi&aacute;o vụ Khoa</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>', 40),
(6, 1, 'Chuyên ngành đào tạo', 'chuyen-nganh-dao-tao', '<p style=\\"text-align: center;\\"><strong><em>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo</em></strong></p>\r\n<table style=\\"margin-left: auto; margin-right: auto;\\" width=\\"340\\">\r\n<thead>\r\n<tr>\r\n<td width=\\"170\\">\r\n<p><strong>Hệ đ&agrave;o tạo</strong></p>\r\n</td>\r\n<td width=\\"170\\">\r\n<p><strong>Ng&agrave;nh đ&agrave;o tạo</strong></p>\r\n</td>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td width=\\"170\\">\r\n<p>Đại học ch&iacute;nh quy</p>\r\n</td>\r\n<td width=\\"170\\">\r\n<p>C&ocirc;ng nghệ th&ocirc;ng tin</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\\"170\\">\r\n<p>Cao đẳng ch&iacute;nh quy</p>\r\n</td>\r\n<td width=\\"170\\">\r\n<p>Tin học ứng dụng</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\\"170\\">\r\n<p>Đại học li&ecirc;n th&ocirc;ng từ Cao đẳng v&agrave; Cao đẳng nghề</p>\r\n</td>\r\n<td width=\\"170\\">\r\n<p>C&ocirc;ng nghệ th&ocirc;ng tin</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\\"170\\">\r\n<p>Cao đẳng nghề</p>\r\n</td>\r\n<td width=\\"170\\">\r\n<p>&nbsp;Lập tr&igrave;nh m&aacute;y t&iacute;nh</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Năm học 2009-2010 l&agrave; năm bắt đầu thực hiện đ&agrave;o tạo theo học chế t&iacute;n chỉ tại Đại học Th&agrave;nh Đ&ocirc; cho c&aacute;c kh&oacute;a đại học, cao đẳng mới nhập học v&agrave; cũng l&agrave; năm Khoa tham gia triển khai Trung t&acirc;m dữ liệu đ&aacute;p ứng y&ecirc;u cầu đổi mới quản l&yacute; gi&aacute;o dục đại học theo tinh thần Chỉ thị số 296/CT-TTg, ng&agrave;y 27-02-2010 của Thủ tướng Ch&iacute;nh phủ.</p>\r\n<p>Khoa CNTT lu&ocirc;n hỗ trợ sinh vi&ecirc;n cố gắng bắt kịp c&aacute;c kỹ năng mềm v&agrave; c&aacute;c khuynh hướng mới trong c&ocirc;ng nghệ th&ocirc;ng tin của c&aacute;c khoa C&ocirc;ng nghệ th&ocirc;ng tin v&agrave; truyền th&ocirc;ng của c&aacute;c trường đại học trong nước v&agrave; thế giới. Đặc biệt c&aacute;c hướng đ&agrave;o tạo</p>\r\n<ul>\r\n<li>Điện to&aacute;n đ&aacute;m m&acirc;y (Cloud computing)</li>\r\n<li>Ph&aacute;t triển nguồn mở (Open source development)</li>\r\n<li>Mạng di động v&agrave; kh&ocirc;ng d&acirc;y (Wireless and Mobile networks)</li>\r\n<li>C&aacute;c ứng dụng ph&acirc;n t&aacute;n trực tuyến (Online Distributed System Application)</li>\r\n<li>Khai th&aacute;c c&ocirc;ng nghệ web mới (New Web Technology Exploitation )</li>\r\n<li>An ninh hệ thống v&agrave; an to&agrave;n mạng (System Safety and Network Security)</li>\r\n</ul>\r\n<p>&nbsp; Việc thực tập tốt nghiệp được định hướng theo c&aacute;c nh&oacute;m chuy&ecirc;n m&ocirc;n bao gồm nh&oacute;m ph&aacute;t triển(developer), nh&oacute;m quản trị ng&acirc;n h&agrave;ng dữ liệu (database administrator), nh&oacute;m quản trị mạng (LAN administraltor), nh&oacute;m an ninh mạng (Network Security), nh&oacute;m quản l&yacute; cơ sở dữ liệu (data center management)...</p>\r\n<p>T&ugrave;y theo điều kiện của mỗi kh&oacute;a học, mỗi nh&oacute;m chuy&ecirc;n m&ocirc;n c&ograve;n được chuy&ecirc;n m&ocirc;n h&oacute;a theo c&aacute;c tiếp cận nghề nghiệp, chẳng hạn&nbsp; như lập tr&igrave;nh (programmer), thử nghiệm (tester), bảo tr&igrave; (support), ph&acirc;n t&iacute;ch (analyst), viết t&agrave;i liệu sử dụng (technical writer)</p>\r\n<p>Phương hướng nghi&ecirc;n cứu khoa học của Khoa C&ocirc;ng nghệ Th&ocirc;ng tin định hướng v&agrave;o thực h&agrave;nh c&ocirc;ng nghệ. C&aacute;c đề t&agrave;i thực tập tốt nghiệp v&agrave; đồ &aacute;n tốt nghiệp của sinh vi&ecirc;n đều gắn với định hướng tr&ecirc;n.</p>\r\n<h4 class=\\"contentheading\\">1. Ng&agrave;nh Mạng m&aacute;y t&iacute;nh</h4>\r\n<p><strong>Giới thiệu:</strong></p>\r\n<p>Chuy&ecirc;n ng&agrave;nh Mạng m&aacute;y t&iacute;nh đ&agrave;o tạo chuy&ecirc;n vi&ecirc;n quản trị mạng v&agrave; hệ thống, được đảm bảo khối lượng kiến thức cần thiết v&agrave; kỹ năng thực h&agrave;nh tốt để thiết kế v&agrave; x&acirc;y dựng c&aacute;c hệ thống mạng đạt ti&ecirc;u chuẩn cao cho doanh nghiệp v&agrave; tổ chức ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin. Sinh vi&ecirc;n được trang bị c&aacute;c kiến thức nền tảng về c&ocirc;ng nghệ mới trong hệ thống m&aacute;y t&iacute;nh, c&aacute;c thiết bị mạng hiện đại, c&aacute;c loại hệ thống mạng cũng như kỹ năng cần thiết trong thiết kế, x&acirc;y dựng v&agrave; bảo tr&igrave; hệ thống mạng.</p>\r\n<p><strong>Nội dung đ&agrave;o tạo:</strong></p>\r\n<p>1. Kiến thức cơ sở</p>\r\n<p>- Mạng m&aacute;y t&iacute;nh</p>\r\n<p>- Kiến tr&uacute;c m&aacute;y t&iacute;nh</p>\r\n<p>2. Kiến thức chung của ng&agrave;nh</p>\r\n<p>- Bảo mật th&ocirc;ng tin</p>\r\n<p>- Ph&acirc;n t&iacute;ch v&agrave; thiết kế mạng</p>\r\n<p>3.Kiến thức chuy&ecirc;n s&acirc;u</p>\r\n<p>- Lập tr&igrave;nh mạng m&aacute;y t&iacute;nh</p>\r\n<p>- An ninh mạng&nbsp;</p>\r\n<p><strong>Cơ hội nghề nghiệp:</strong></p>\r\n<p>- Chuy&ecirc;n vi&ecirc;n thiết kế v&agrave; x&acirc;y dựng hệ thống mạng</p>\r\n<p>- Chuy&ecirc;n vi&ecirc;n Quản trị mạng</p>\r\n<p>- Chuy&ecirc;n vi&ecirc;n bảo mật c&aacute;c loại hệ thống mạng</p>\r\n<p>-Chuy&ecirc;n vi&ecirc;n tư vấn, đ&agrave;o tạo nh&acirc;n lực quản trị mạng v&agrave; hệ thống</p>\r\n<h4 class=\\"contentheading\\">2. Ng&agrave;nh C&ocirc;ng nghệ phần mềm</h4>\r\n<p><strong>Giới thiệu:</strong></p>\r\n<p>C&ocirc;ng nghệ phần mềm l&agrave; ng&agrave;nh học d&agrave;nh cho những ai y&ecirc;u th&iacute;ch trực tiếp tham gia v&agrave;o c&ocirc;ng việc l&agrave;m phần mềm, s&aacute;ng tạo n&ecirc;n những sản phẩm mang t&iacute;nh ứng dụng cao.</p>\r\n<p>Theo học ng&agrave;nh n&agrave;y, c&aacute;c bạn c&oacute; cơ hội l&agrave;m việc trực tiếp với những kh&aacute;ch h&agrave;ng l&agrave; những tập đo&agrave;n, c&ocirc;ng ty lớn trong ng&agrave;nh c&ocirc;ng nghệ phần mềm vốn đang ph&aacute;t triển s&ocirc;i động tr&ecirc;n to&agrave;n thế giới.</p>\r\n<p><strong>Nội dung đ&agrave;o tạo:</strong></p>\r\n<ul>\r\n<li>Kiến thức cơ sở</li>\r\n</ul>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Kỹ thuật lập tr&igrave;nh</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Hệ điều h&agrave;nh</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Mạng m&aacute;y t&iacute;nh</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Bảo mật th&ocirc;ng tin</p>\r\n<ul>\r\n<li>Kiến thức chung của ng&agrave;nh</li>\r\n</ul>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - C&ocirc;ng nghệ phần mềm</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Lập tr&igrave;nh web</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Bảo mật th&ocirc;ng tin</p>\r\n<ul>\r\n<li>Kiến thức chuy&ecirc;n s&acirc;u</li>\r\n</ul>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Kiến tr&uacute;c v&agrave; thiết kế phần mềm</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Quản l&yacute; dự &aacute;n phần mềm</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Lập tr&igrave;nh tr&ecirc;n thiết bị di động</p>', 30),
(7, 1, 'Cơ sở vật chất', 'co-so-vat-chat', '<p>Khoa CNTT được Nh&agrave; trường trang bị 8 ph&ograve;ng thực h&agrave;nh với hơn 300 m&aacute;y t&iacute;nh được kết nối mạng Internet v&agrave; đầy đủ trang thiết bị hiện đại đảm bảo về cả số lượng v&agrave; chất lượng cho c&ocirc;ng t&aacute;c giảng dạy v&agrave; nghi&ecirc;n cứu khoa học.</p>\r\n<p>C&aacute;c m&aacute;y t&iacute;nh lu&ocirc;n được cập nhật c&aacute;c phần mềm mới nhất phục vụ cho c&ocirc;ng t&aacute;c gảng dạy v&agrave; học tập.</p>\r\n<p><strong>Một số h&igrave;nh ảnh cơ sở vật chất của khoa:</strong></p>\r\n<figure class=\\"image\\"><img src=\\"/source/users/1/images/khoa-cntt/co_so_vat_chat/vp-khoa.jpg?1461376475908\\" alt=\\"vp-khoa\\" />\r\n<figcaption><em>Văn ph&ograve;ng khoa</em></figcaption>\r\n</figure>\r\n<figure class=\\"image\\"><img src=\\"/source/users/1/images/khoa-cntt/co_so_vat_chat/thuc-hanh.jpg?1461376525488\\" alt=\\"thuc-hanh\\" />\r\n<figcaption><em>Sinh vi&ecirc;n trong giờ học thực h&agrave;nh m&aacute;y t&iacute;nh</em></figcaption>\r\n</figure>\r\n<figure class=\\"image\\"><img src=\\"/source/users/1/images/khoa-cntt/co_so_vat_chat/bao_ve_do_an.jpg?1461376579445\\" alt=\\"\\" />\r\n<figcaption><em>Sinh vi&ecirc;n tr&igrave;nh b&agrave;y bảo vệ đồ &aacute;n tốt nghiệp</em></figcaption>\r\n</figure>\r\n<figure class=\\"image\\"><img src=\\"/source/users/1/images/khoa-cntt/co_so_vat_chat/hoi_thao_khoa_hoc.jpg?1461376606458\\" alt=\\"\\" />\r\n<figcaption><em>Hội thảo khoa học của sinh vi&ecirc;n trong khoa</em></figcaption>\r\n</figure>\r\n<figure class=\\"image\\"><img src=\\"/source/users/1/images/khoa-cntt/co_so_vat_chat/phong_thi_trac_nghiem.jpg?1461376629964\\" alt=\\"\\" />\r\n<figcaption><em>Ph&ograve;ng thi trắc nghiệm</em></figcaption>\r\n</figure>\r\n<figure class=\\"image\\"><img src=\\"/source/users/1/images/khoa-cntt/co_so_vat_chat/thuc_hanh_phan_cung.jpg?1461376651771\\" alt=\\"thuc_hanh_phan_cung\\" />\r\n<figcaption><em>Ph&ograve;ng thực h&agrave;nh phần cứng</em></figcaption>\r\n</figure>\r\n<figure class=\\"image\\"><img src=\\"/source/users/1/images/khoa-cntt/co_so_vat_chat/thuc_hanh_mya_tinh.jpg?1461376674801\\" alt=\\"thuc_hanh_mya_tinh\\" />\r\n<figcaption><em>Ph&ograve;ng thực h&agrave;nh m&aacute;y t&iacute;nh</em></figcaption>\r\n</figure>\r\n<figure class=\\"image\\"><img src=\\"/source/users/1/images/khoa-cntt/co_so_vat_chat/thuc_hanh_may_tinh_2.jpg?1461376709187\\" alt=\\"thuc_hanh_may_tinh_2\\" />\r\n<figcaption><em>Ph&ograve;ng thực h&agrave;nh m&aacute;y t&iacute;nh</em></figcaption>\r\n</figure>\r\n<figure class=\\"image\\"><img src=\\"/source/users/1/images/khoa-cntt/co_so_vat_chat/thuc_hanh_may_tinh_3.jpg?1461376727713\\" alt=\\"thuc_hanh_may_tinh_3\\" />\r\n<figcaption><em>Ph&ograve;ng thực h&agrave;nh m&aacute;y t&iacute;nh</em></figcaption>\r\n</figure>', 40),
(9, 2, 'Chuyển giao công nghệ', 'chuyen-giao-cong-nghe', '<p>Sản phẩm c&ocirc;ng nghệ</p>', 2);

