--
-- Table structure for table `post`
--

CREATE TABLE IF NOT EXISTS `post` (
  `post_id` int(11) NOT NULL AUTO_INCREMENT,
  `post_category` int(11) NOT NULL,
  `post_name` varchar(500) COLLATE utf8_unicode_ci NOT NULL,
  `post_content` text COLLATE utf8_unicode_ci NOT NULL,
  `post_slug` varchar(500) COLLATE utf8_unicode_ci NOT NULL,
  `post_keyword` varchar(500) COLLATE utf8_unicode_ci NOT NULL,
  `post_description` varchar(500) COLLATE utf8_unicode_ci NOT NULL,
  `post_image` varchar(500) COLLATE utf8_unicode_ci NOT NULL,
  `post_publisher` int(11) NOT NULL,
  `post_feature` tinyint(2) NOT NULL,
  `post_status` tinyint(2) NOT NULL,
  `created` datetime NOT NULL,
  `updated` datetime NOT NULL,
  PRIMARY KEY (`post_id`),
  UNIQUE KEY `doc_id` (`post_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=36 ;

